import { useState } from "react";
import IngredientSearchbox from "./IngredientSearchbox";
import styled from 'styled-components';

const SaveButton = styled.div`
  cursor: pointer;
  position: absolute;
  top: -10px;
  right: -20px;
  width: 32px;
  height: 32px;
  padding: 0;
  z-index: 10;
`;

function RecipeIngredientForm ( {onSearch, ingredients}){
    const [unit, setUnit] = useState('')
    const [ingredient, setIngredient] = useState('')
    const [quantity, setQuantity] = useState('')


    return (
        <div className="flex">

                <IngredientSearchbox onSearch = {onSearch} ingredients = {ingredients} setIngredient={setIngredient}/>

                <input type="text" placeholder="Measurement Unit"
                value={unit}
                style={{width: "20%"}}
                onChange={(e) => setUnit(e.target.value)}
                />

                <input type="text" 
                style={{width: "20%"}}
                placeholder="quantity"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)} />


        </div>
    )

}

export default RecipeIngredientForm
