import React from 'react'
import RecipeIngredientForm from './RecipeIngredientForm'
import styled from 'styled-components';
import Modal from '../Modal';
import { useState } from 'react';


const Button = styled.button`
  min-width: 100px;
  padding: 16px 32px;
  border-radius: 4px;
  border: none;
  background: #141414;
  color: #fff;
  font-size: 24px;
  cursor: pointer;
`;

const RecipeForm = ({onSearch, ingredients}) => {

    const [showModal, setShowModal] = useState(false);

    const openModal = () => {
      setShowModal(prev => !prev);
    };

    return (
        <form >
            {/* <RecipeIngredientForm onSearch = {onSearch} ingredients = {ingredients}/> */}
            <button class="bg-green-500 text-white rounded-md px-8 py-2 text-base font-medium hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-300"
		    id="open-btn">
		        Open Modal
	        </button>
            <Modal showModal={showModal} setShowModal={setShowModal} />
            <Button onClick={openModal}>I'm a modal</Button>
        </form>
    )
}

export default RecipeForm
