import React from 'react'
import RecipeIngredientForm from './RecipeIngredientForm'
import styled from 'styled-components';
import Modal from '../Modal';
import { useState } from 'react';
import { NavLink as Link } from 'react-router-dom';
import {closeModal, showIngredientModal} from '../../actions'
import { useSelector, useDispatch } from 'react-redux';

const Button = styled.button`
  min-width: 100px;
  padding: 16px 32px;
  border-radius: 4px;
  border: none;
  background: #141414;
  color: #fff;
  font-size: 24px;
  cursor: pointer;
`;


const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const openIngredientModal = () => {
  dispatch(showIngredientModal)
}

const modalOpen = () => {
  const modalVal =  useSelector(state => this.state.Modal);
  switch (modalVal) {
    case "INGREDIENT":
      return true;
    default: 
      return false;
  }
}
const showModal = modalOpen();

const RecipeForm = ({onSearch, ingredients}) => {

    return (
      <>
      (
        <Modal /> 
      )
        <form >
            {/* <RecipeIngredientForm onSearch = {onSearch} ingredients = {ingredients}/> */}

      <button className="bg-green-500 text-white rounded-md px-8 py-2 text-base font-medium hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-300"
        id="open-btn">
        Open Modal
      </button>


       <Button onClick={openIngredientModal}>I'm a modal</Button> 
    </form>
      </>
  )
}

export default RecipeForm
