import React from 'react'
import RecipeIngredientForm from './RecipeIngredientForm'
import styled from 'styled-components';
import {Modal} from '../Modal';
import { useState } from 'react';
import { NavLink as Link } from 'react-router-dom';
import {closeModal, showIngredientModal} from '../../actions'
import { useSelector, useDispatch } from 'react-redux';

const Button = styled.button`
  min-width: 100px;
  padding: 16px 32px;
  border-radius: 4px;
  border: none;
  background: #141414;
  color: #fff;
  font-size: 24px;
  cursor: pointer;
`;


const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;


const RecipeForm = ({onSearch, ingredients}) => {
  const dispatch = useDispatch();

  const openIngredientModal = () => {
    dispatch(showIngredientModal())
  }

  const modalOpen = () => {
    const modalVal =  useSelector(state => state.Modal);
    switch (modalVal) {
      case "INGREDIENT":
        return true;
      default: 
        return false;
    }
  }
  const showModal = modalOpen();

    return (
      <>
        {/* <Modal showModal={showModal} />  */}
        <form >
            {/* <RecipeIngredientForm onSearch = {onSearch} ingredients = {ingredients}/> */}




    </form>
    {modalOpen() ? (<Modal showModal={showModal} />) : null}
    {showModal ? (<h1>Open </h1>) : <h1>Close </h1>}
    <Button onClick={openIngredientModal}>I'm a modal</Button> 

      </>
  )
}

export default RecipeForm
